sourcesContent:
import React from 'react';
import {
  Box,
  Container,
  Typography,
  Button,
  Grid,
  Card,
  CardContent,
  CardMedia,
  AppBar,
  Toolbar,
  IconButton,
  Avatar,
  Paper,
  Divider,
  Modal,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
} from '@mui/material';
import {
  ArrowForward,
  Notifications,
  Settings,
  Menu as MenuIcon,
  TrendingUp,
  AccountBalance,
  ShowChart,
  Speed,
  AutoGraph,
  Psychology,
  RocketLaunch,
  SwapHoriz,
  ArrowUpward,
  ArrowDownward,
  Email,
  Store,
  EmojiEvents,
  Insights,
  Newspaper,
  ChevronLeft,
  ChevronRight,
  LinkedIn,
  Twitter,
  YouTube,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { keyframes } from '@emotion/react';
import { useState, useEffect, useRef } from 'react';

// Animation keyframes for chart line and candlesticks
const glow = keyframes`
  0% { box-shadow: 0 0 8px #43ea4a44; }
  50% { box-shadow: 0 0 24px #43ea4a99; }
  100% { box-shadow: 0 0 8px #43ea4a44; }
`;
const candlestickGrow = keyframes`
  0% { height: 20px; }
  50% { height: 60px; }
  100% { height: 20px; }
`;
const priceFade = keyframes`
  0% { opacity: 0.5; }
  50% { opacity: 1; }
  100% { opacity: 0.5; }
`;

const carouselFade = keyframes`
  0% { opacity: 0; transform: translateY(30px) scale(0.97); }
  100% { opacity: 1; transform: translateY(0) scale(1); }
`;

const heroCarouselSlides = [
  {
    icon: <Email sx={{ fontSize: 48, color: 'secondary.main', mb: 1 }} />,
    title: 'Pro Newsletter Alerts',
    desc: 'Get real-time earnings alerts, technical signals, and exclusive research delivered to your inbox.'
  },
  {
    icon: <Store sx={{ fontSize: 48, color: 'secondary.main', mb: 1 }} />,
    title: 'Algorithm Marketplace',
    desc: 'Browse, buy, and deploy trading algorithms from top developers. Automate your edge.'
  },
  {
    icon: <EmojiEvents sx={{ fontSize: 48, color: 'secondary.main', mb: 1 }} />,
    title: 'Trading Tournaments',
    desc: 'Compete in public trading tournaments powered by blockchain. Climb the leaderboard and win rewards.'
  },
  {
    icon: <Insights sx={{ fontSize: 48, color: 'secondary.main', mb: 1 }} />,
    title: 'Sentiment & Financial Analysis',
    desc: 'Gauge market sentiment and access advanced financial analytics for smarter trades.'
  },
  {
    icon: <Newspaper sx={{ fontSize: 48, color: 'secondary.main', mb: 1 }} />,
    title: 'News & Research',
    desc: 'Stay ahead with curated news, research, and real-time market updates.'
  }
];

const HomePage: React.FC = () => {
  const navigate = useNavigate();
  const [heroIndex, setHeroIndex] = useState(0);
  const timerRef = useRef<NodeJS.Timeout | null>(null);
  const [carouselDirection, setCarouselDirection] = useState<'left' | 'right'>('right');

  // Auto-advance carousel every 10 seconds
  useEffect(() => {
    if (timerRef.current) clearTimeout(timerRef.current);
    timerRef.current = setTimeout(() => {
      setHeroIndex((i) => (i + 1) % heroCarouselSlides.length);
    }, 10000);
    return () => { if (timerRef.current) clearTimeout(timerRef.current); };
  }, [heroIndex]);

  const nextHero = () => {
    setCarouselDirection('right');
    setHeroIndex((i) => (i + 1) % heroCarouselSlides.length);
  };
  const prevHero = () => {
    setCarouselDirection('left');
    setHeroIndex((i) => (i - 1 + heroCarouselSlides.length) % heroCarouselSlides.length);
  };

  // Newsletter modal state
  const [newsletterOpen, setNewsletterOpen] = useState(false);
  const handleNewsletterOpen = () => setNewsletterOpen(true);
  const handleNewsletterClose = () => setNewsletterOpen(false);

  return (
    <Box sx={{ flexGrow: 1 }}>
      {/* Header with Login button, no hamburger */}
      <AppBar position="static" color="transparent" elevation={0} sx={{ bgcolor: 'transparent', boxShadow: 'none', py: 2 }}>
        <Toolbar sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', px: { xs: 1, md: 3 } }}>
          <Typography
            variant="h6"
            sx={{
              fontWeight: 800,
              color: 'white',
              letterSpacing: 1,
              fontSize: { xs: 22, md: 28 },
              fontFamily: 'Georgia, serif',
            }}
          >
            RobberBaron
          </Typography>
          <Button
            variant="outlined"
            color="primary"
            sx={{
              borderColor: '#fff',
              color: '#fff',
              fontWeight: 700,
              borderRadius: 2,
              px: 3,
              py: 1,
              fontSize: { xs: 14, md: 16 },
              borderWidth: 2,
              borderStyle: 'solid',
            }}
            href="/login"
          >
            Login
          </Button>
        </Toolbar>
      </AppBar>

      {/* Hero Section with Carousel */}
      <Box
        sx={{
          position: 'relative',
          color: 'white',
          py: { xs: 6, md: 12 },
          overflow: 'hidden',
          background: '#111',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'none',
            zIndex: -1,
          },
        }}
      >
        <Container maxWidth="xl" sx={{ px: { xs: 1, sm: 2, md: 4 } }}>
          <Grid container spacing={4} alignItems="center" direction={{ xs: 'column-reverse', md: 'row' }}>
            <Grid item xs={12} md={6} sx={{ textAlign: { xs: 'center', md: 'left' }, mb: { xs: 4, md: 0 } }}>
              <Typography variant="h2" component="h1" gutterBottom sx={{ fontWeight: 800, fontSize: { xs: 32, md: 54 }, lineHeight: 1.1, color: 'white' }}>
                Trade with Confidence
              </Typography>
              <Typography variant="h5" sx={{ mb: 4, opacity: 0.95, fontSize: { xs: 16, md: 24 }, color: 'white', fontWeight: 400 }}>
                Advanced trading tools and expert insights to help you navigate the markets
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: { xs: 'column', sm: 'row' }, gap: 2, justifyContent: { xs: 'center', md: 'flex-start' } }}>
                <Button
                  variant="contained"
                  color="primary"
                  sx={{ bgcolor: '#111', color: '#fff', fontWeight: 700, borderRadius: 3, px: 4, py: 1.5, fontSize: { xs: 15, md: 18 }, '&:hover': { bgcolor: '#222', color: '#fff' }, display: 'flex', alignItems: 'center', gap: 1 }}
                  startIcon={<Newspaper sx={{ color: '#fff', fontSize: 22 }} />}
                  onClick={() => navigate('/newsletter')}
                >
                  Newsletter
                </Button>
                <Button
                  variant="outlined"
                  color="primary"
                  sx={{ borderColor: '#111', color: '#111', fontWeight: 700, borderRadius: 3, px: 4, py: 1.5, fontSize: { xs: 15, md: 18 }, bgcolor: '#fff', '&:hover': { bgcolor: '#eee', color: '#111', borderColor: '#111' } }}
                >
                  Sign Up
                </Button>
              </Box>
            </Grid>
            <Grid item xs={12} md={6}>
              <Box sx={{ position: 'relative', width: '100%', height: { xs: 320, md: 420 }, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                {/* Carousel Card with smooth transition */}
                <Box sx={{ position: 'relative', width: '100%', maxWidth: 520, minHeight: { xs: 260, md: 340 }, display: 'flex', alignItems: 'center', justifyContent: 'center', overflow: 'hidden' }}>
                  <Box
                    key={heroIndex}
                    sx={{
                      position: 'absolute',
                      width: '100%',
                      height: '100%',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      transition: 'opacity 1.2s cubic-bezier(.4,1.4,.6,1), transform 1.2s cubic-bezier(.4,1.4,.6,1)',
                      opacity: 1,
                      zIndex: 1,
                      animation: `${carouselDirection === 'right' ? 'fadeInRight' : 'fadeInLeft'} 1.2s`,
                      '@keyframes fadeInRight': {
                        from: { opacity: 0, transform: 'translateX(80px) scale(0.97)' },
                        to: { opacity: 1, transform: 'translateX(0) scale(1)' },
                      },
                      '@keyframes fadeInLeft': {
                        from: { opacity: 0, transform: 'translateX(-80px) scale(0.97)' },
                        to: { opacity: 1, transform: 'translateX(0) scale(1)' },
                      },
                    }}
                  >
                    <Paper elevation={0} sx={{
                      bgcolor: '#fff',
                      borderRadius: 10,
                      p: { xs: 3, md: 7 },
                      minHeight: { xs: 220, md: 340 },
                      width: '100%',
                      maxWidth: 520,
                      textAlign: 'center',
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      justifyContent: 'center',
                      boxShadow: '0 8px 48px #0008, 0 2px 16px #0008',
                      position: 'relative',
                      background: '#fff',
                      border: '1.5px solid #222',
                    }}>
                      <Box sx={{ mb: 2, display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        {React.cloneElement(heroCarouselSlides[heroIndex].icon, { color: 'inherit', sx: { fontSize: 56, color: '#111', mb: 1 } })}
                      </Box>
                      <Typography variant="h4" sx={{ fontWeight: 800, color: 'black', mb: 2, fontSize: { xs: 22, md: 34 } }}>
                        {heroCarouselSlides[heroIndex].title}
                      </Typography>
                      <Typography variant="h6" color="text.secondary" sx={{ fontSize: { xs: 14, md: 20 }, fontWeight: 500, color: 'black' }}>
                        {heroCarouselSlides[heroIndex].desc}
                      </Typography>
                    </Paper>
                  </Box>
                </Box>
                {/* Carousel Controls and Dots BELOW the card, arrows on either side of dots */}
                <Box sx={{
                  position: 'absolute',
                  left: 0,
                  right: 0,
                  bottom: { xs: -40, sm: -48, md: -56 },
                  display: 'flex',
                  justifyContent: 'center',
                  alignItems: 'center',
                  gap: 2,
                  zIndex: 2,
                }}>
                  <IconButton onClick={() => { prevHero(); }} sx={{ color: '#111', bgcolor: '#fff', '&:hover': { bgcolor: '#eee', color: '#111' }, width: 44, height: 44, border: '1.5px solid #222' }}>
                    <ChevronLeft sx={{ fontSize: 32 }} />
                  </IconButton>
                  <Box sx={{ display: 'flex', gap: 1, mx: 2 }}>
                    {heroCarouselSlides.map((_, idx) => (
                      <Box key={idx} sx={{ width: 14, height: 14, borderRadius: '50%', bgcolor: idx === heroIndex ? '#111' : '#bbb', border: idx === heroIndex ? '2px solid #111' : '2px solid #bbb', transition: 'bgcolor 0.2s, border 0.2s' }} />
                    ))}
                  </Box>
                  <IconButton onClick={() => { nextHero(); }} sx={{ color: '#111', bgcolor: '#fff', '&:hover': { bgcolor: '#eee', color: '#111' }, width: 44, height: 44, border: '1.5px solid #222' }}>
                    <ChevronRight sx={{ fontSize: 32 }} />
                  </IconButton>
                </Box>
